import styles from "./Colors.module.css"
import { useState } from "react"

function Colors ({returnColor, func}){
    const [color, setColor] = useState("")

    function handleClick (e) {
        console.log(e.target)
    }

    return(
        <div className={styles.colors} onClick={func}>
            <div className={styles.circle} id={styles.green} onClick={handleClick} ></div>
            <div className={styles.circle} id={styles.yellow} onClick={handleClick}></div>
            <div className={styles.circle} id={styles.orange} onClick={handleClick}></div>
            <div className={styles.circle} id={styles.red} onClick={handleClick}></div>
            <div className={styles.circle} id={styles.purple} onClick={handleClick}></div>
        </div>
    )
}

export default Colors
