import { useState } from "react"

function CardNote ({title, type, text, color}){

    const cards = [
        {
            title: "Primeiro Card",
            type: "Nota",
            text: "Esse é o texto do primeiro card",
            color: "#FF5733"
        },
        {
            title: "Segundo Card",
            type: "Aviso",
            text: "Esse é o texto do segundo card",
            color: "#33FF57"
        },
        {
            title: "Terceiro Card",
            type: "Lembrete",
            text: "Esse é o texto do terceiro card",
            color: "#3357FF"
        }
        // dados forjados para teste, depois disso lembrar de ja começar a trabalhar nessa parte da api
    ];

    return(
        <div className="note">
            <div>
                <div className="circle" style={{backgroundColor: `${color}`}}></div>
                <h1>{title}</h1>
            </div>
            <div>
                <p>{text}</p>
            </div>
        </div>
    )
}

export default CardNote
