import styles from "./CurrentColor.module.css"
import { useState } from "react"

function CurrentColor ({Ccolor, func}){

    let [color, setColor] = useState("")

    function handleClick () {
        setColor(Ccolor)
    }

    return(
        <>
            <div className={styles.colors} onClick={func}>
                <div className={styles.circle} id={styles.green} style={{backgroundColor: color}} onClick={() => handleClick()}></div>
            </div>
        </>
    )
}

export default CurrentColor
